import React from 'react';
import './App.css';
import Login from './login'; // Asegúrate de importar el componente de Login



function App() {

  const [user, setUser] = useState(null); // Agregamos un estado para el usuario


  return (
    <div className="App">
      <header className="App-header">
        <h1>Welcome to eco-logic</h1>
        {user ? (
          <div>
            <h2>Bienvenido, {user.displayName}</h2>
            <img src={user.photoURL} alt="Usuario" />
          </div>
        ) : (
        <Login setUser={setUser} /> // Aquí estamos usando el componente Login */}
        )}
        </header>
    </div>
  );
}
export default App;
