import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';

const UserPage = ({ user }) => {
  const { id } = useParams(); // Get ID from the URL
  const [datetime, setDatetime] = useState('');

  //Function to update date and time
  const updateDateTime = () => {
    const date = new Date();
    const options = { 
      year: 'numeric', 
      month: 'long', 
      day: 'numeric', 
      hour: '2-digit', 
      minute: '2-digit', 
      second: '2-digit' 
    };
    setDatetime(date.toLocaleString('en-US', options));
  };

  // Update time every second
  useEffect(() => {
    const interval = setInterval(updateDateTime, 1000);
    updateDateTime(); 
    return () => clearInterval(interval); 
  }, []);

  return (
    <div style={styles.body}>
      <div style={styles.header}>
        <div style={styles.hello}>Hello, <span id="username">{user ? user.displayName : 'User'}</span></div>
        <div style={styles.datetime}>{datetime}</div>
      </div>

      <div style={styles.link}>
        <h2>Welcome to the Eco-Logic App</h2>
        <p>Click below to go to your profile page:</p>
        <a href={`/user/${user ? user.uid : id}`} style={styles.linkText}>Go to your profile</a>
      </div>
    </div>
  );
};

const styles = {
  body: {
    fontFamily: 'Arial, sans-serif',
    margin: 0,
    padding: 0,
    display: 'flex',
    justifyContent: 'space-between',
    alignItems: 'center',
    height: '100vh',
    backgroundColor: '#f4f4f4',
  },
  header: {
    position: 'absolute',
    top: '10px',
    left: '10px',
    right: '10px',
    display: 'flex',
    justifyContent: 'space-between',
    width: 'calc(100% - 20px)',
    backgroundColor: '#282c34',
    color: 'white',
    padding: '10px',
  },
  hello: {
    fontSize: '18px',
    fontWeight: 'bold',
  },
  datetime: {
    fontSize: '18px',
    fontWeight: 'bold',
  },
  link: {
    marginTop: '50px',
    textAlign: 'center',
  },
  linkText: {
    color: '#61dafb',
    textDecoration: 'none',
  },
};

export default UserPage;
