import React, { useState } from 'react';
import './App.css';
import Login from './login'; // Ensure you import the Login component
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom'; // Import Navigate for redirection
import UserPage from './UserPage'; // UserPage component

function App() {
  const [user, setUser] = useState(null); // State to hold user info

  return (
    <Router>
      <div className="App">
        <header className="App-header">
          <h2>Welcome to eco-logic</h2>

          <Routes>
            {/* Main route with conditional rendering based on user */}
            <Route
              path="/"
              element={user ? <UserPage user={user} /> : <Login setUser={setUser} />}
            />
            {/* Dynamic route for user page based on user ID */}
            <Route
              path="/user/:id"
              element={
                user ? (
                  <UserPage user={user} />
                ) : (
                  <Navigate to="/" /> // Redirect to login if no user is logged in
                )
              }
            />
          </Routes>
        </header>
      </div>
    </Router>
  );
}

export default App;
