import React, { useState } from 'react';

const DayToDayPage = () => {
  // Initialize state for carbon emissions input by the user
  const [emissions, setEmissions] = useState({
    drivingCar: 4.6,
    heatingHome: 7.2,
    electricityUsage: 5.1,
  });

  // Handle input changes for each activity
  const handleInputChange = (event, activity) => {
    const { value } = event.target;
    // Update the state based on the activity name (e.g., drivingCar, heatingHome)
    setEmissions((prevEmissions) => ({
      ...prevEmissions,
      [activity]: value,
    }));
  };

  return (
    <div style={styles.page}>
      <h3>Day-to-Day Carbon Emissions</h3>
      <table style={styles.table}>
        <thead>
          <tr>
            <th>Activity</th>
            <th>Carbon Emissions (kg/day)</th>
          </tr>
        </thead>
        <tbody>
          <tr>
            <td>Driving a car (15 miles)</td>
            <td>
              <input
                type="number"
                value={emissions.drivingCar}
                onChange={(e) => handleInputChange(e, 'drivingCar')}
                style={styles.input}
              />
            </td>
          </tr>
          <tr>
            <td>Heating home</td>
            <td>
              <input
                type="number"
                value={emissions.heatingHome}
                onChange={(e) => handleInputChange(e, 'heatingHome')}
                style={styles.input}
              />
            </td>
          </tr>
          <tr>
            <td>Electricity usage</td>
            <td>
              <input
                type="number"
                value={emissions.electricityUsage}
                onChange={(e) => handleInputChange(e, 'electricityUsage')}
                style={styles.input}
              />
            </td>
          </tr>
          {/* Add more activities as needed */}
        </tbody>
      </table>
      <button onClick={() => alert('Calculate Carbon Emissions')}>Calculate Carbon Emissions</button>
    </div>
  );
};

const styles = {
  page: {
    textAlign: 'center',
    marginTop: '50px',
    color: '#fff',
    backgroundColor: '#282c34',
    padding: '20px',
  },
  table: {
    margin: '20px auto',
    width: '80%',
    borderCollapse: 'collapse',
  },
  th: {
    backgroundColor: '#61dafb',
    padding: '10px',
  },
  td: {
    textAlign: 'center',
    padding: '10px',
    border: '1px solid #ddd',
  },
  input: {
    width: '80px',
    padding: '5px',
    fontSize: '14px',
    textAlign: 'center',
    borderRadius: '5px',
    border: '1px solid #ccc',
  },
};

export default DayToDayPage;
